/*
we can injectproperties from our end also
we can access the highest hierarchy (ie. object) ang inject property into that, then that property will be available and can be accesed by string, arrays etc...
so all the levels have the power as it is injected in the highest level

wt if we give the the power to array (lower in hierarchy), then will the power be available with obj by default??
NO. The obj wont get the power which is defined for array as the power can be passed from higher levels to lower levels but not vice versa
*/

// inheritance

const User = {
    name: "chai",
    email: "chai@google.com"
}

const Teacher = {
    makeVideo: true
}

const TeachingSupport = {
    isAvailable: false
}

const TASupport = {
    makeAssignment: 'JS assignment',
    fullTime: true,
    __proto__: TeachingSupport
}

Teacher.__proto__ = User

// modern syntax
Object.setPrototypeOf(TeachingSupport, Teacher)

let anotherUsername = "ChaiAurCode     "

String.prototype.trueLength = function(){
    console.log(`${this}`);
    console.log(`True length is: ${this.trim().length}`);
}

anotherUsername.trueLength()
"hitesh".trueLength()
"iceTea".trueLength()